import React, { Component } from "react";
import { connect } from "react-redux";
import { getAllSalonsFromApi } from "../../actions/salonAction";
import {
  MDBBtn,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBCardTitle,
  MDBCardText,
  MDBCol,
  MDBContainer,
  MDBRow,
  MDBInput,
} from "mdbreact";

export class Indexclient extends Component {
  componentDidMount() {
    this.props.getallsalons();
  }
  render() {
    return (
      <MDBContainer>
        <MDBRow>
          <MDBCol size="4">
            <MDBInput label="Nom de salon" outline />
          </MDBCol>
          <MDBCol size="4">
            <MDBInput label="Ville" outline />
          </MDBCol>
          <MDBCol className="mt-3">
            <MDBBtn color="pink">Rechecher</MDBBtn>
          </MDBCol>
        </MDBRow>
        <div className="d-flex align-content-start flex-wrap  mt-5 ">
          {this.props.salons.map((el) => (
            <div style={{ maxWidth: "15rem" }}>
              <MDBCard>
                <MDBCardImage
                  className="img-fluid"
                  src="https://mdbootstrap.com/img/Mockups/Lightbox/Thumbnail/img%20(67).jpg"
                  waves
                />
                <MDBCardBody>
                  <MDBCardTitle>Card title</MDBCardTitle>
                  <MDBCardText>
                    Some quick example text to build on the card title and make
                    up the bulk of the card's content.
                  </MDBCardText>
                  <MDBBtn href="#">Click</MDBBtn>
                </MDBCardBody>
              </MDBCard>
            </div>
          ))}
        </div>
      </MDBContainer>
    );
  }
}

const mapStateToProps = (state) => ({
  salons: state.salons,
});

const mapDispatchToProps = (dispatch) => ({
  getallsalons: () => dispatch(getAllSalonsFromApi()),
});
export default connect(mapStateToProps, mapDispatchToProps)(Indexclient);
