import { GET_ALL_SALONS, ADD_SALON } from "./types";
import Axios from "axios";

/* get all salons */

export const getAllSalons = (payload) => ({
  type: GET_ALL_SALONS,
  payload,
});
//get all salons from db
export function getAllSalonsFromApi() {
  console.log("ff");
  return (dispatch) =>
    Axios.get("http://localhost:4000/pro/getallsalons").then((res) => {
      console.log(res.data);
      dispatch(getAllSalons(res.data));
    });
}
// add  salon
export const addsalon = (payload) => ({
  type: ADD_SALON,
  payload,
});
//add salon to database
export function addsalontoDB(salon) {
  console.log(salon);
  return (dispatch) =>
    Axios.post("http://localhost:4000/pro/addsalon", salon).then((res) =>
      dispatch(addsalon(res.data))
    );
}
//Update salon
export function updateSaloninDB(id, salon) {
  return () =>
    Axios.put("http://localhost:4000/pro/update/" + id, salon).then((res) =>
      console.log("salon deja mis a jour")
    );
}
