import Axios from "axios";

export function LoginWithToken(user) {
  return () =>
    Axios.post("http://localhost:4000/auth/", user, {
      withCredentials: true,
    }).then((res) => {
      var role = JSON.stringify(res.data.role);
      console.log(role);
      if (role.role === "client") window.location.pathname = "/client";
      else if (role === "pro") window.location.pathname = "/pro";
    });
}
