import Axios from "axios";
import decode from "jwt-decode";
//Update salon
export function LoginWithToken(user) {
  return () =>
    Axios.post("http://localhost:4000/auth/", user, {
      withCredentials: true,
    }).then((res) => {
      const decoded = decode(req.coockies.token);
      if (decoded.role === "client") window.location.pathname = "/client";
      else if (decoded.role === "pro") window.location.pathname = "/pro";
    });
}
