import Axios from "axios";
import decode from "jwt-decode";
//Update salon
export function LoginWithToken(client) {
  return () =>
    Axios.post("http://localhost:4000/auth/", client).then((res) => {
      const decoded = decode(res.data);
      console.log(decoded.role);
      if (decoded.role === "client") window.location.pathname = "/client";
      else if (decoded.role === "pro") window.location.pathname = "/pro";
    });
}
