import React, { Component } from "react";
import { connect } from "react-redux";
import { getAllSalonsFromApi } from "../../actions/salonAction";
import {
  MDBBtn,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBCardTitle,
  MDBCardText,
  MDBCol,
  MDBContainer,
  MDBRow,
  MDBInput,
} from "mdbreact";

export class Indexclient extends Component {
  componentDidMount() {
    this.props.getallsalons();
  }
  render() {
    return (
      <MDBContainer>
        <MDBRow>
          <MDBCol size="2">
            <MDBInput label="Nom de salon" outline />
          </MDBCol>
          <MDBCol size="2">
            <MDBInput label="Ville" outline />
          </MDBCol>
          <MDBCol size="2">
            <select className="browser-default custom-select mt-2">
              <option>Ville</option>
              <option value="1">Option 1</option>
              <option value="2">Option 2</option>
              <option value="3">Option 3</option>
            </select>
          </MDBCol>
        </MDBRow>
        <hr></hr>
        <div className="d-flex align-content-start flex-wrap  mt-5 ">
          {this.props.salons.map((el) => (
            <div style={{ maxWidth: "15rem" }} className="ml-5 mb-5">
              <MDBCard>
                <MDBCardImage className="img-fluid" src={el.img[0]} waves />
                <MDBCardBody>
                  <MDBCardTitle>{el.name}</MDBCardTitle>
                  <MDBCardText>
                    {el.street},{el.city}
                  </MDBCardText>
                  <MDBBtn href="#">Réservez</MDBBtn>
                </MDBCardBody>
              </MDBCard>
            </div>
          ))}
        </div>
      </MDBContainer>
    );
  }
}

const mapStateToProps = (state) => ({
  salons: state.salons,
});

const mapDispatchToProps = (dispatch) => ({
  getallsalons: () => dispatch(getAllSalonsFromApi()),
});
export default connect(mapStateToProps, mapDispatchToProps)(Indexclient);
