import Axios from "axios";
import decode from "jwt-decode";
//Update salon
export function LoginWithToken(client) {
  console.log("eee");
  return () =>
    Axios.post("http://localhost:4000/auth/", client, {
      withCredentials: true,
    }).then((res) => {
      const decoded = decode(res.data);
    });
}
