import React, { Component } from "react";
import { connect } from "react-redux";
import { getAllSalonsFromApi } from "../../actions/salonAction";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBView,
  MDBMask,
  MDBBtn,
} from "mdbreact";
export class Indexclient extends Component {
  componentDidMount() {
    this.props.getallsalons();
  }
  render() {
    return (
      <div className="d-flex">
        {this.props.salons.map((el) => (
          <MDBView>
            <img
              src="https://www.idgraphique.fr/asset/tmp/slide/habillage-facade-magasin-coiffeur-enseigne-680.jpg"
              className="img-fluid"
              alt=""
            />
            <MDBMask className="flex-center" pattern={3}>
              <p className="white-text">Inscrivez vous vite </p>
              <div>
                <MDBBtn>sfjdhfjd</MDBBtn>
              </div>
            </MDBMask>
          </MDBView>
        ))}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  salons: state.salons,
});

const mapDispatchToProps = (dispatch) => ({
  getallsalons: () => dispatch(getAllSalonsFromApi()),
});
export default connect(mapStateToProps, mapDispatchToProps)(Indexclient);
