import { GET_ALL_SALONS, ADD_CLIENT } from "./types";
import Axios from "axios";

/* get all salons */

export const getAllSalons = (payload) => ({
  type: GET_ALL_SALONS,
  payload,
});

export function getAllSalonsFromApi() {
  console.log("ff");
  return (dispatch) =>
    Axios.get("http://localhost:4000/pro/getallsalons").then((res) => {
      console.log(res.data);
      dispatch(getAllSalons(res.data));
    });
}

// add  salon
export const addclient = (payload) => ({
  type: ADD_CLIENT,
  payload,
});

export function addclienttoDB(client) {
  return (dispatch) =>
    Axios.post("http://localhost:4000/client/addclient", client).then((res) =>
      dispatch(addclient(res.data))
    );
}
