import Axios from "axios";

export function LoginWithToken(user) {
  return () =>
    Axios.post("http://localhost:4000/auth/", user, {
      withCredentials: true,
    }).then((res) => {
      console.log(res.data);
      localStorage.setItem("client", res.data);

      if (res.data.role === "client") window.location.pathname = "/client";
      else if (res.data.role === "pro") window.location.pathname = "/pro";
    });
}
