import Axios from "axios";

export function LoginWithToken(user) {
  return () =>
    Axios.post("http://localhost:4000/auth/", user, {
      withCredentials: true,
    }).then((res) => {
      console.log(res.data);
      const token = req.get("token");
      console.log(token);
    });
}

