import React, { Component } from "react";
import { connect } from "react-redux";
import { getAllSalonsFromApi } from "../../actions/salonAction";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBView,
  MDBMask,
  MDBBtn,
  MDBInput,
} from "mdbreact";
export class Indexclient extends Component {
  componentDidMount() {
    this.props.getallsalons();
  }
  render() {
    return (
      <MDBContainer>
        <MDBRow>
          <MDBCol size="4">
            <MDBInput label="Nom de salon" outline />
          </MDBCol>
          <MDBCol size="4">
            <MDBInput label="Ville" outline />
          </MDBCol>
          <MDBCol className="mt-3">
            <MDBBtn color="pink">Rechecher</MDBBtn>
          </MDBCol>
        </MDBRow>
        <div className="d-flex align-content-start flex-wrap  mt-5 ">
          {this.props.salons.map((el) => (
            <MDBCol md="4">
              <MDBView>
                <img
                  src="https://www.idgraphique.fr/asset/tmp/slide/habillage-facade-magasin-coiffeur-enseigne-680.jpg"
                  className="img-fluid"
                  alt=""
                />
                <MDBMask className="flex-center" pattern={0}>
                  <div>
                    <MDBBtn color="pink">Réservez</MDBBtn>
                  </div>
                </MDBMask>
                <p className="white-text">{el.street}</p>
              </MDBView>
            </MDBCol>
          ))}
        </div>
      </MDBContainer>
    );
  }
}

const mapStateToProps = (state) => ({
  salons: state.salons,
});

const mapDispatchToProps = (dispatch) => ({
  getallsalons: () => dispatch(getAllSalonsFromApi()),
});
export default connect(mapStateToProps, mapDispatchToProps)(Indexclient);
