import React, { Component } from "react";
import { connect } from "react-redux";
import {
  MDBTable,
  MDBTableBody,
  MDBTableHead,
  MDBBtn,
  MDBContainer,
} from "mdbreact";

import { getallMeetsfromDB } from "../../../actions/meetAction";
import { GetoneProFromDB } from "../../../actions/salonAction";
export class meetingslist extends Component {
  componentDidMount() {
    this.props.getallmeets();
  }
  render() {
    return (
      <MDBContainer className="bg">
        <div className="mt-3">
          <h1>Mes rendez-vous</h1>
        </div>
        <hr></hr>
        <MDBTable responsive>
          <MDBTableHead>
            <tr>
              <th></th>
              <th>Salon</th>
              <th>Heure</th>
              <th>Date</th>
              <th>Status</th>
              <th>Supprimer</th>
            </tr>
          </MDBTableHead>
          <MDBTableBody>
            {this.props.meets.map((el) => (
              <tr>
                <td>1</td>
                <td>
                  <strong>{this.props.getUserinfos(el.clientID).name} </strong>
                </td>

                <td>{el.time}</td>
                <td>
                  {el.day}/{el.month}/{el.year}{" "}
                </td>
                <td>{el.status}</td>
                <td>
                  <MDBBtn flat color="danger">
                    Annuller
                  </MDBBtn>
                </td>
              </tr>
            ))}
          </MDBTableBody>
        </MDBTable>
      </MDBContainer>
    );
  }
}

const mapStateToProps = (state) => ({
  meets: state.meets,
});

const mapDispatchToProps = (dispatch) => ({
  getallmeets: () => dispatch(getallMeetsfromDB()),
  getUserinfos: (userid) => dispatch(GetoneProFromDB(userid)),
});

export default connect(mapStateToProps, mapDispatchToProps)(meetingslist);
