import { GET_ALL_SALONS } from "./types";
import Axios from "axios";

/* get all salons */

export const getAllSalons = (payload) => ({
  type: GET_ALL_SALONS,
  payload,
});

export function getAllSalonsFromApi() {
  return (dispatch) =>
    Axios.get("http://localhost:3001/salons").then((res) =>
      dispatch(getAllSalons(res.data))
    );
}
